import React, { useState } from 'react';
import './RegistrationForm.css'; // Import the CSS file for styling

const RegistrationForm = () => {
  // State variables to store phone number and selected organization
  const [phoneNumber, setPhoneNumber] = useState('');
  const [selectedOrg, setSelectedOrg] = useState('');

  // Sample organizations list
  const organizations = [
    'Cardiovascular Health Education Campaign'
    // Add more organizations as needed
  ];

  // Event handler for phone number input change
  const handlePhoneNumberChange = (event) => {
    setPhoneNumber(event.target.value);
  };

  // Event handler for organization dropdown change
  const handleOrgChange = (event) => {
    setSelectedOrg(event.target.value);
  };

  // Event handler for form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    // Perform actions with the collected data (e.g., send to server)
    console.log('Phone Number:', phoneNumber);
    console.log('Selected Organization:', selectedOrg);
    // You can add further processing or API calls here
  };

  return (
    <div className="form-container"> {/* Wrap the form in a container */}
      <form onSubmit={handleSubmit} className="registration-form">
      <div>
        <label htmlFor="phone">Enter your Phone Number:</label>
        <input
          type="tel"
          id="phone"
          value={phoneNumber}
          onChange={handlePhoneNumberChange}
          required
        />
      </div>
      <div>
        <label htmlFor="organizations">Select your Organization:</label>
        <select
          id="organizations"
          value={selectedOrg}
          onChange={handleOrgChange}
          required
        >
          <option value="">Select an organization</option>
          {organizations.map((org, index) => (
            <option key={index} value={org}>
              {org}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default RegistrationForm;
